# In-memory store for post comments and shares
comments_store = {post['id']: [] for post in posts_data}
shares_store = {post['id']: 0 for post in posts_data}

@posts_bp.route("/", methods=["GET"])
def get_posts():
    posts_with_counts = []
    for post in posts_data:
        new_post = post.copy()
        new_post['comments_count'] = len(comments_store.get(post['id'], []))
        new_post['shares_count'] = shares_store.get(post['id'], 0)
        posts_with_counts.append(new_post)
    return jsonify(posts_with_counts)

@posts_bp.route("/<int:id>/like", methods=["POST"])
def like_post(id):
    for post in posts_data:
        if post['id'] == id:
            post['likes'] += 1
            return jsonify({"message": f"Post {id} liked!", "likes": post['likes']})
    return jsonify({"error": "Post not found"}), 404

@posts_bp.route("/<int:id>/comment", methods=["POST"])
def comment_post(id):
    data = request.get_json()
    comment_text = data.get("comment", "")
    if id in comments_store:
        comments_store[id].append(comment_text)
        return jsonify({"message": f"Comment added to post {id}", "comment": comment_text, "comments_count": len(comments_store[id])})
    else:
        return jsonify({"error": "Invalid post id"}), 404

@posts_bp.route("/<int:id>/share", methods=["POST"])
def share_post(id):
    if id in shares_store:
        shares_store[id] += 1
        return jsonify({"message": f"Post {id} shared!"})
